      hobbyinterest: null,
      comments: null,
      //
      agreement: null,
      alart1: false,
      alart2: false,
      alart3: false,
      alart4: false,
      showagreement: true,
      shownewaccount: false,
      showpersnalinfo: false,
      showschedule: false,
      showlanguagewant: false,
      showlanguageteach: false,
