      dropdown_want_level2: ['A0', 'A1', 'A2', 'B1', 'B2', 'C1', 'C2'],
      dropdown_teach_level1: ['native', 'over C1'],
      dropdown_teach_level2: ['native', 'over C1'],
      dropdown_teach_level3: ['native', 'over C1'],
      headers: [{
        text: 'TIMELINE',
        align: 'left',
