      }]
    };
  },
  methods: {
    backhome: function backhome() {
      this.$router.push('/home');
    },
    gotonewaccount: function gotonewaccount() {
      if (this.agreement == "agreement-1") {
        this.shownewaccount = true;
        this.showagreement = false;
      } else if (this.agreement == "agreement-2") {
        this.shownewaccount = false;
        this.$router.push('/');
      }
    },
    gotopersonalinfo: function gotopersonalinfo() {
      var value1 = this.titechemail && this.titechemail.trim();
      var value2 = this.password && this.password.trim();

      if (value1 != null && value1 != "" && value2 != null && value2 != "" && this.titechemail.match('titech.ac.jp') != null) {
        this.shownewaccount = false;
        this.showpersnalinfo = true;
        this.alart1 = false;
      } else {
        this.alart1 = true;
      }
    },
    backtonewaccount: function backtonewaccount() {
      this.shownewaccount = true;
      this.showpersnalinfo = false;
    },
    gotoschedule: function gotoschedule() {
      var value1 = this.username && this.username.trim();
      var value2 = this.studentid && this.studentid.trim();
      var value3 = this.gender;
      var value4 = this.nation;
      var value5 = this.degree;

      if (value1 != null && value1 != "" && value2 != null && value2 != "" && value3 != null && value4 != null && value5 != null) {
        this.showpersnalinfo = false;
        this.showschedule = true;
        this.alart2 = false;
      } else {
        this.alart2 = true;
      }
    },
    backtopersonalinfo: function backtopersonalinfo() {
      this.showpersnalinfo = true;
      this.showschedule = false;
    },
    gotolanguagewant: function gotolanguagewant() {
      this.showschedule = false;
      this.showlanguagewant = true;
    },
    backtoschedule: function backtoschedule() {
      this.showschedule = true;
      this.showlanguagewant = false;
    },
    gotolanguageteach: function gotolanguageteach() {
      if (this.learnlang1 != null && this.learnlang1level != null) {
        this.showlanguagewant = false;
        this.showlanguageteach = true;
        this.alart3 = false;
      } else {
        this.alart3 = true;
      }
    },
    backtolanguagewant: function backtolanguagewant() {
      this.showlanguagewant = true;
      this.showlanguageteach = false;
    },
    gotofindinfo: function gotofindinfo() {
      if (this.teachlang1 != null && this.teachlang1level != null) {
        this.showlanguageteach = false;
        this.showfindinfo = true;
        this.alart4 = false;
      } else {
        this.alart4 = true;
      }
    },
    backtolanguageteach: function backtolanguageteach() {
      this.showlanguageteach = true;
      this.showfindinfo = false;
    },
    confirm: function confirm() {
      this.showfindinfo = false;
      this.showconfirm = true;
    },
    backtofindinfo: function backtofindinfo() {
      this.showfindinfo = true;
      this.showconfirm = false;
    },
    signUp: function signUp() {
      var _this = this;

      firebase.auth().createUserWithEmailAndPassword(this.titechemail, this.password).then(function (username) {
        var db = firebase.firestore();
        db.collection("users").add({
          titechemail: _this.titechemail,
          username: _this.username,
          email2: _this.email2,
          studentid: _this.studentid,
          gender: _this.gender,
          nation: _this.nation,
          major: _this.major,
          degree: _this.degree,
          weekend: _this.weekend,
          campus: _this.campus,
          frequency: _this.frequency,
          learnlang1: _this.learnlang1,
          learnlang1level: _this.learnlang1level,
          learnlang2: _this.learnlang2,
          learnlang2level: _this.learnlang2level,
          teachlang1: _this.teachlang1,
          teachlang1level: _this.teachlang1level,
          teachlang2: _this.teachlang2,
          teachlang2level: _this.teachlang2level,
          teachlang3: _this.teachlang3,
          teachlang3level: _this.teachlang3level,
          hobbyinterest: _this.hobbyinterest,
          comments: _this.comments
        }).then(function (docRef) {
          console.log("Document written with ID: ", docRef.id);
        }).catch(function (error) {
          console.error("Error adding document: ", error);
        });
        alert('Created account:', _this.titechemail);
        console.log(username);

        _this.$router.push('/mypage');
      }).catch(function (error) {
        alert(error.message);
      });
    }
  }
};
